mapper.insert:

	select SEQ_EMAIL_TASK.nextval as ID from DUAL


insert into EMAIL_TASK (ID, SUBJECT, CONTENT,
SENDER_EMAIL, RECEIVER_EMAIL, CREATOR,
CREATED_TIME, STATUS, STATUS_TIME
)
values (#{id,jdbcType=DECIMAL}, #{subject,jdbcType=VARCHAR}, #{content,jdbcType=CLOB},
#{senderEmail,jdbcType=VARCHAR}, #{receiverEmail,jdbcType=VARCHAR}, #{creator,jdbcType=DECIMAL},
#{createdTime,jdbcType=TIMESTAMP}, #{status,jdbcType=DECIMAL}, #{statusTime,jdbcType=TIMESTAMP}
)
